mapper.select:
	select * from(   select rownum  rn,a.* from(

            SELECT * FROM product
            order by product_date desc
            
 )  a   )where rn between #{first}  and #{last}

mapper.update:
UPDATE PRODUCT
PRODUCT_ID = #{productId}, PRODUCT_NAME = #{productName}, PRODUCT_PRICE = #{productPrice}, PRODUCT_IMG = #{productImg}, PRODUCT_DIR = #{productDir}, PRODUCT_TEXT = #{productText}, PRODUCT_CATEGORY = #{productCategory}
WHERE PRODUCT_ID = #{productId}

mapper.select:
SELECT PRODUCT_CATEGORY
FROM PRODUCT
WHERE PRODUCT_CATEGORY = #{productCategory}
